# RNA Codon Table mapping codons to amino acids
codon_table = {
    'AUG': 'M', 'UGG': 'W', 'UUU': 'F', 'UUC': 'F', 'UUA': 'L', 'UUG': 'L',
    'CUU': 'L', 'CUC': 'L', 'CUA': 'L', 'CUG': 'L', 'AUU': 'I', 'AUC': 'I',
    'AUA': 'I', 'GUU': 'V', 'GUC': 'V', 'GUA': 'V', 'GUG': 'V', 'UCU': 'S',
    'UCC': 'S', 'UCA': 'S', 'UCG': 'S', 'CCU': 'P', 'CCC': 'P', 'CCA': 'P',
    'CCG': 'P', 'ACU': 'T', 'ACC': 'T', 'ACA': 'T', 'ACG': 'T', 'GCU': 'A',
    'GCC': 'A', 'GCA': 'A', 'GCG': 'A', 'UAU': 'Y', 'UAC': 'Y', 'CAU': 'H',
    'CAC': 'H', 'CAA': 'Q', 'CAG': 'Q', 'AAU': 'N', 'AAC': 'N', 'AAA': 'K',
    'AAG': 'K', 'GAU': 'D', 'GAC': 'D', 'GAA': 'E', 'GAG': 'E', 'UGU': 'C',
    'UGC': 'C', 'CGU': 'R', 'CGC': 'R', 'CGA': 'R', 'CGG': 'R', 'AGU': 'S',
    'AGC': 'S', 'AGA': 'R', 'AGG': 'R', 'GGU': 'G', 'GGC': 'G', 'GGA': 'G',
    'GGG': 'G', 'UAA': 'Stop', 'UAG': 'Stop', 'UGA': 'Stop'
}

# Function to translate RNA string to protein string
def translate_rna_to_protein(rna_sequence):
    protein = []
    
    # Iterate through the RNA sequence in steps of 3 nucleotides (1 codon)
    for i in range(0, len(rna_sequence), 3):
        codon = rna_sequence[i:i+3]  # Extract the codon
        amino_acid = codon_table.get(codon, '')
        
        # Stop translating if a stop codon is found
        if amino_acid == 'Stop':
            break
        
        # Append the amino acid to the protein string if valid
        if amino_acid:
            protein.append(amino_acid)
    
    # Return the protein string
    return ''.join(protein)

# Sample input RNA sequence
rna_sequence = ""

# Get the protein string
protein_string = translate_rna_to_protein(rna_sequence)

# Output the result
print(protein_string)
